import React from 'react';
import ReactDOM from 'react-dom';
import Heading from './Heading';
import Para from './Para';

ReactDOM.render(
    <>
    <Heading></Heading>
    <Para />
    <ol>
        <li>Apple</li>
        <li>Mango</li>
        <li>Peach</li>
    </ol>
    </>,
    document.getElementById('root')
);


//In this tutorial we are learning about react component they are nothing but a function which can be used
