import React from 'react';
import ReactDOM from 'react-dom';

// How to render Multiple elements in React...
// render method takes one single JSX element to write multiple JSX elemtent we need to wrap the elements to one
// that's why we wrap both h1 and p element to a div now inside div we can write any number of elements.

// ReactDOM.render(
// <div>
//   <h1> hello world!! </h1>
//   <p>I am from GL Bajaj </p> 
// </div>,
// document.getElementById('root'));



// But in react v16 and above it is possible for render() to return an array of elements
// like ['hello','hi'];

ReactDOM.render(
  [
    <h1> hello world!! </h1>,
    <p>I am from GL Bajaj Institute </p> 
  ],
  document.getElementById('root'));
