import React, { useState } from 'react';

const App = () => {
    const defaultName = "user";
    const [fname, upName] = useState(defaultName);
    const [updatedname, setFullName] = useState(" ");

    const display = ()=>{
        setFullName(fname);
    };

    const inputEvent = (event) => {
        // console.log(event.target.value);
        upName(event.target.value);
    }

    return (
        <>
            <h1>Hello {updatedname}</h1>
            <input type='text' placeholder="Enter your name"  onChange={inputEvent} />
            <button type='submit' onClick = {display} >Hello</button>
        </>
    );
};


export default App;


// use camelCase while declaring events like onClick onMouseEnter onDoubleClick  
// and don't add () after the function name while calling the function in events 
// use hooks while changing the state with events ...
