import React, { useState } from 'react';

const App = () => {
    const [fullName, setFullName] = useState({
        fName: "",
        lName: "",
        email: "",
        phone: ""
    });

    const inputEvent = (event) => {
        // console.log(event.target.value);
        const value = event.target.value;
        const name = event.target.name;
        setFullName((prevValue) => {
            if (name === 'fName') {
                return ({
                    fName: value,
                    lName: prevValue.lName,
                    email: prevValue.email,
                    phone: prevValue.phone
                });
            }
            else if (name === 'lName') {
                return ({
                    fName: prevValue.fName,
                    lName: value,
                    email: prevValue.email,
                    phone: prevValue.phone
                });
            }
            else if (name === 'email') {
                return ({
                    fName: prevValue.fName,
                    lName: prevValue.lName,
                    email: value,
                    phone: prevValue.phone
                });
            }
            else if (name === 'phone') {
                return ({
                    fName: prevValue.fName,
                    lName: prevValue.lName,
                    email: prevValue.email,
                    phone: value
                });
            }
        });
    }

    //*                    Explaination of inputEvent()
    // what we do in this func is, we just grab the value of two attribute 1. value of input element and 2. name of input element
    // in if-else condition, we check if the name contains 'fname'  then we set the value of fName only in useState() and gives lName its prevValue by returning the object
    // and vice-versa if name contains 'lName'

    const displayName = (event) => {
        event.preventDefault();
    };


    // to prevent the default behaviour of refresh page after submits the data we use event.preventDefault();
    // coz of this page doesnot refresh




    return (
        <>
            <h1>Hello {fullName.fName} {fullName.lName} </h1>
            <p> {fullName.email} </p>
            <p> {fullName.phone} </p>
            <form onSubmit={displayName} >
                <input type='text' name='fName' placeholder="Enter your name" onChange={inputEvent} />
                <input type='text' name='lName' placeholder="Enter your last name" onChange={inputEvent} />
                <input type='email' name='email' placeholder="Enter your Email-id" onChange={inputEvent} />
                <input type='number' name='phone' placeholder="Enter your Phone Number" onChange={inputEvent} />
                <button type='submit' >Click Me !!</button>
            </form>
        </>
    );
};


export default App;


// Handling complex Multiple Input
 //! First Change
// what we do here is, we make an object in useState(), so that we can take multiple values in single useState(),  instead of making multiple useState()....everytime we need to change a state
// when we use objects in useState(), then during the updation of any value ie setName() func.... we have to update the whole object, 
// if we did not want to do so then, we easily return the prevValue to that key which we don't want to update..
//* Like we did above in if-else loop we update one of the names ie fName or lName and return the other with its prevValue but we return the whole object


 //! Second Change
// AND we also place the value of all input in a single function to reduce the code 

//! Third Change (i think we use prevValue only when we use objects)
// we also use a function inside the UpdatedState() or setState() and pass a parameter into it..... 
// which can take the prevValue of initialState like prevValue.fName give the prevValue which is blank ie " "
