import React, { useState } from 'react';

const App = () => {
    const [fname, upName] = useState(" ");
    const [lname,setLastName] = useState(" ");
    const [updatedname, setUpdatedName] = useState(" ");
    const [updatedLastname,setFullName] = useState(" ");

    const inputEvent = (event) => {
        // console.log(event.target.value);
        upName(event.target.value);
    }

    const inputEventTwo = (event)=>{
        setLastName(event.target.value);
    }

    const displayName = (event)=>{
        event.preventDefault();
        setUpdatedName(fname);
        setFullName(lname);
    };

    
// to prevent the default behaviour of refresh page after submits the data we use event.preventDefault();
// coz of this page doesnot refresh




    return (
        <>
            <h1>Hello {updatedname} {updatedLastname} </h1>
            <form onSubmit = {displayName} >
            <input type='text' placeholder="Enter your name"  onChange={inputEvent} />
            <input type='text' placeholder="Enter your last name"  onChange={inputEventTwo} />
            <input type='email' placeholder="Enter your Email-id"  onChange={inputEventTwo} />
            <button type='submit' >Click Me !!</button> 
            </form>
        </>
    );
};


export default App;
