import React, {useState} from 'react';
import Header from './Header';
import CreateNote from './CreateNote';
import Note from './Note';
import Footer from './Footer';

const App = () => {
    const [keepValue,setValue] = useState({
        title: " ",
        content: " "
    });
    const [showValue,setShowValue] = useState({
        title: " ",
        content: " "
    });
    const Prevent = (event) => {
        event.preventDefault();
    }

    const Value = (event) => {
        const { value, name } = event.target;
        setValue((prevValue) => {
            return ({
                ...prevValue,
                [name]: value
            });
        })};
        
    const AddNote = () => {

        // const {title,content} = keepValue;
        console.log(title + " " + content);
        setShowValue(keepvalue.title,content);

    }
    const DeleteNote = () => {
        alert('deleted');
    }
    return (

        <>
            <Header />
            <CreateNote prevfunc={Prevent} addfunc={AddNote} inputfunc={Value} />
            <Note delfunc={DeleteNote} title={showValue.title} content = {showValue.content} />
            <Footer />
        </>
    )
};

export default App;
