import React from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';


const CreateNote = (props) => {

    return (
        <form onChange={props.prevfunc}>
            <br></br>
            <input className='input-1' type='text'  name ={props.titl} placeholder='Title' onChange={props.inputfunc} />
            <br></br>
            <textarea className='input-2' name = {props.contnt} placeholder='Write a note..' onChange={props.inputfunc} rows='4' cols = '4' />
            <Button color="secondary" onClick = {props.addfunc}><AddIcon /></Button>
        </form>
    );
}

export default CreateNote;
