import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const date = new Date().getHours();
let greet = "";
if(date > 1 && date < 7)
{
    ReactDOM.render(
        <>
            <h1 style={{color:'orange'}}>Hello sir,
            <span style={{color:'blue'}}>Good Morning</span>
            </h1>
        </>,
        document.getElementById('root')
    )
}
else if(date >8 && date < 19)
{
    ReactDOM.render(
        <>
            <h1 style={{color:'orange'}}>Hello sir,
            <span style={{color:'purple'}}>Good After-noon</span>
            </h1>
        </>,
        document.getElementById('root')
    )
}
else{
    ReactDOM.render(
        <>
            <h1 style={{color:'orange'}}>Hello sir,
            <span style={{color:'black'}}>Good Night</span>
            </h1>
        </>,
        document.getElementById('root')
    )
}


