const App = () => {
    const [stat, setstat] = useState(0);
    const increment = ()=>{
        setstat(stat+1);
    }
    // const decrement = ()=>{
    //     if(stat === 0){
    //         alert('zero value reached can go beyond ');
    //     }
    //     else{
    //         setstat(stat-1);
    //     }
    // }
    return (
        <>
            <form>
                <h1>{stat}</h1>
                <button onClick={increment}>Increment</button>
                {/* <button onClick={decrement}>Decrement</button> */}
            </form>
        </>
    )
};

export default App;
