import React, {useState} from 'react';

const App = ()=>{
    const name = "User";
    const[fname,upName] = useState(name)
    return (
        <>
        <h1>Hello {name}</h1>
        <form>
           <input type='text' placeholder="Enter your name"/>
           <input type='submit' /> 
        </form>
        </>
    );
};


export default App;


