import React from 'react';
import Cards from './Cards';
import Seriesdata from './Seriesdata';


function App() {
    return (
        <>
            <h1> List of Top 5 Series in 2020.. </h1>

            <div className='container'>
                <Cards
                    imgsrc= {Seriesdata[0].imgsrc}
                    title=  {Seriesdata[0].title}
                    sname=  {Seriesdata[0].sname}
                    slink=  {Seriesdata[0].slink}
                    clName = {Seriesdata[0].clName}
                />
                <Cards
                    imgsrc={Seriesdata[1].imgsrc}
                    title={Seriesdata[1].title}
                    sname={Seriesdata[1].sname}
                    slink={Seriesdata[1].slink}
                    clName = {Seriesdata[1].clName}
                />
                <Cards
                    imgsrc={Seriesdata[2].imgsrc}
                    title={Seriesdata[2].title}
                    sname={Seriesdata[2].sname}
                    slink={Seriesdata[2].slink}
                    clName = {Seriesdata[2].clName}
                />
                <Cards
                    imgsrc='images/vamp_diaries.jpg'
                    title='Amazon Prime'
                    sname='Vampire Diaries'
                    slink='https://www.primevideo.com/detail/The-Vampire-Diaries/0HG2QOPONEONZPP2ZXNMA6M8Q7'
                    clName = 'amazon'
                />

            </div>
        </>
    );
}

export default App;



