import React, {useState} from 'react';

const App = ()=>{
    const name = "aksht";
    return (
        <>
        <h1>Hello {name}</h1>
        <form>
           <input type='text' placeholder="Enter your name"/> 
        </form>
        </>
    );
};


export default App;


