import React, {useState} from 'react';
import Header from './Header';
import CreateNote from './CreateNote';
import Note from './Note';
import Footer from './Footer';

const App = () => {
    const [value,setValue] = useState({
        title: " ",
        content: " "
    });
    const Prevent = (event) => {
        event.preventDefault();
    }

    const Value = (event) => {
        console.log(event.target.value);
        const { value, name } = event.target;
        setValue((prevValue) => {
            return ({
                ...prevValue,
                [name]: value
            });
        })};
        
    const AddNote = () => {
        alert('hi');
    }
    const DeleteNote = () => {
        alert('deleted');
    }
    return (

        <>
            <Header />
            <CreateNote prevfunc={Prevent} addfunc={AddNote} inputfunc={Value} titl = 'title' contnt = 'content'  />
            <Note delfunc={DeleteNote} title={value.title} content = {value.content} />
            <Footer />
        </>
    )
};

export default App;
