import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const date = new Date(2020,7,5,12);
date.getHours();

let greet = "";

const cssStyle = {};

if(date >= 1 && date < 12)
{
    greet = 'Good Morning';
    cssStyle.color = 'green';
}
else if(date >= 12 && date < 19)
{
    greet = 'Good Afternoon';
    cssStyle.color = 'yellow';
}
else{
    greet = 'Good Night';
    cssStyle.color = 'black';
}


ReactDOM.render(
    <>
        <h1 style={{color:'orange'}}>Hello sir,
        <span style={cssStyle}>{greet}</span>
        </h1>
    </>,
    document.getElementById('root')
)
