import React, { useState } from 'react';

const App = () => {
    let count = 0;
    const [state, setstate] = useState(count);
    const increment = ()=>{
        count++;
        return setstate(state = count);
    }
    const decrement = ()=>{
        if(state === 0){
            alert('zero value reached can go beyond ');
        }
        else{
            count--;
            return setstate(state = count);
        }
    }
    return (
        <>
            <form>
                <input type='number'>{state}</input>
                <button onClick={increment}>Increment</button>
                <button onClick={decrement}>Decrement</button>
            </form>
        </>
    )
};

export default App;
