import React, {useState} from 'react';
import Header from './Header';
import CreateNote from './CreateNote';
import Note from './Note';
import Footer from './Footer';

const App = () => {
    const [keepValue,setValue] = useState({
        title: " ",
        content: " "
    });
    const [showValue,setShowValue] = useState([]);
    const Prevent = (event) => {
        event.preventDefault();
    }

    const Value = (event) => {
        const { value, name } = event.target;
        setValue((prevValue) => {
            return ({
                ...prevValue,
                [name]: value
            });
        })};
        
    const AddNote = () => {
        setShowValue((prevValue)=>{
            return [...prevValue,keepValue]
        });
        console.log(showValue);

    }
    const DeleteNote = () => {
        alert('deleted');
    }
    return (

        <>
            <Header />
            <CreateNote prevfunc={Prevent} addfunc={AddNote} inputfunc={Value} />
            {showValue.map((value, index)=>{
                <Note delfunc={DeleteNote} key={index} id={index} title={value.title} content = {value.content} />
            })}
            <Footer />
        </>
    )
};

export default App;
