import React, { useState } from 'react';

const App = () => {
    const [stat, setstat] = useState(0);
    const increment = ()=>{
        setstat(stat+1);
    }
    const decrement = ()=>{
        if(stat === 0){
            alert("zero value reached can't go beyond ");
        }
        else{
            setstat(stat-1);
        }
    }
    return (
        <>

                <h1>{stat}</h1>
                <button onClick={increment}>Increment</button>
                <button onClick={decrement}>Decrement</button>
        </>
    )
};

export default App;
