import React from 'react';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';

const Note = (props) => {
    return (
        <>
            <div className='lists'>
            <h3>{props.title}</h3>
            <h3>{props.content}</h3>
            <Button color="primary" onClick={() => {
                    props.delfunc(props.id)
                }
                }><DeleteIcon /></Button>
            </div>
        </>
    );
}

export default Note;
