import React, { useState } from 'react';

const App = () => {
    const [currNote, setNote] = useState('');
    const [items, setItems] = useState([]);

    const notesValue = (event) => {
        var notes = event.target.value;
        setNote(notes);
    };

    const addNotes = (events) => {
        events.preventDefault();
        setItems((prevValue)=>{
            return [...prevValue,currNote];
        });
    };

    return (
        <>
            <div className='main_div'>
                <div className='center-div'>
                    <h1>TO do List</h1>
                    <input type='text' name='notes' placeholder='Add a note' onChange={notesValue} />
                    <button onClick={addNotes}>Add Notes</button>
                    <ol>
                        {/* <li>{currNote}</li> */}

                        {
                            items.map((value) => {
                                return (<li>{value}</li>);
                            })
                        }
                    </ol>
                </div>
            </div>
        </>
    )
};

export default App;
