import React, { useState } from 'react';
import SeriesData from './Seriesdata';
import Cards from './Cards';
import { div } from 'prelude-ls';

const App = () => {
    const [currNote, setNote] = useState('');

    const notesValue = (events) => {
        var notes = events.target.value;
        setNote(notes);

    };

    const addNotes = (events) => {
        events.preventDefault();
        SeriesData.push(currNote);
        div.innerHtml = <Cards />;
        // console.log(SeriesData);
    };

    return (
        <>
           <div>
           <form>
                <input type='text' name='notes' placeholder='Add a note' onChange={notesValue} />
                <button onClick={addNotes}>Add Notes</button>
            </form>
           </div>
        </>
    )
};

export default App;
