import React, {useState, useEffect} from 'react';


const App = () => {
    let counting = 0;
    const [count, setcount] = useState(counting);
    const [counts, setcounts] = useState(counting);
    useEffect(() => {
        alert('hi');
        });
    // useEffect(() => {
    //     effect
    //     return () => {
    //         cleanup
    //     }
    // }, [input])

    const increment = ()=>{
        setcount(count+1);   
    }
    const incrementt = ()=>{
        setcounts(counts+1);
    }
    return (

        <>
        <h1>{count}</h1>
        <button onClick={increment}>click me</button>
        <br></br>
        <h1>{counts}</h1>
        <button onClick={incrementt}>click me</button>
        </>
    )
};

export default App;




//* Here we understand what useEffect Hook is
// by using this hook, you tell react that your component needs to do something
// after render. React will remember the func you passed call it later after performing DOM updates.
