import React from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';


const CreateNote = (props) => {

    return (
        <form onChange={props.prevfunc}>
            <br></br>
            <input type='text' value ={props.titleval}  name="title" placeholder='Title' autoComplete = 'off' onChange={props.inputfunc} />
            <br></br>
            <input name="content" value = {props.contentval} placeholder='Write a note..' onChange={props.inputfunc}  />
            <Button color="secondary" onClick = {props.addfunc}><AddIcon /></Button>
        </form>
    );
}

export default CreateNote;
