import React from 'react';

let textColor = {};

let date = new Date(2020, 5, 5, 16);
date = date.getHours();

let greet = '';

if (date > 1 & date < 12) {
    greet = 'Good Morning';
    textColor.color = 'skyblue';
}
else if (date >= 12 & date < 16) {
    greet = 'Good Afternoon';
    textColor.color = 'yellow';
}
else if (date >= 16 & date < 20) {
    greet = 'Good Evening';
    textColor.color = 'orange';
}
else {
    greet = 'Good Night';
    textColor.color = 'darkblue';
}


function Greet() {
    return (
        <h1 style={{ color: 'pink' }}>Hello Sir, <span style={textColor} >{greet}</span></h1>
    );
}

export default Greet;
